async function getUncachableGitHubClient() {
  const accessToken = await getAccessToken();
  return new Octokit({ auth: accessToken });
}
async function fetchRepositoryData(repoUrls) {
  try {
    const octokit = await getUncachableGitHubClient();
    const repoData = [];
    for (const repoUrl of repoUrls) {
      const [owner, repo] = repoUrl.split("/");
      try {
        const { data } = await octokit.repos.get({
          owner,
          repo
        });
        repoData.push({
          id: data.id.toString(),
          name: data.name,
          description: data.description || "No description available",
          html_url: data.html_url,
          homepage: data.homepage,
          stargazers_count: data.stargazers_count,
          forks_count: data.forks_count,
          language: data.language,
          topics: data.topics || [],
          updated_at: data.updated_at,
          owner: data.owner.login,
          repo: data.name
        });
      } catch (error) {
        console.error(`Error fetching repo ${repoUrl}:`, error);
        repoData.push({
          id: repoUrl.replace("/", "-"),
          name: repo,
          description: "Repository data temporarily unavailable",
          html_url: `https://github.com/${repoUrl}`,
          homepage: null,
          stargazers_count: 0,
          forks_count: 0,
          language: "Unknown",
          topics: [],
          updated_at: (/* @__PURE__ */ new Date()).toISOString(),
          owner,
          repo
        });
      }
    }
    return repoData;
  } catch (error) {
    console.error("Error fetching GitHub data:", error);
    throw error;
  }
}

// server/routes.ts
async function registerRoutes(app2) {
  app2.get("/api/github/repositories", async (req, res) => {
    try {
      const repoUrls = [
        "asadbmirza/Thumbmarks",
        "asadbmirza/Planetze-Mobile-Application",
        "RishiJain905/flashcard-project",
        "asadbmirza/tetris-assembly",
        "asadbmirza/Multiplayer-Online-Asynchronous-Battleship",
        "asadbmirza/Pokemon-Battle-Simulator"
      ];
      const repositories = await fetchRepositoryData(repoUrls);
      res.json(repositories);
    } catch (error) {
      console.error("Error fetching repositories:", error);
      res.status(500).json({ error: "Failed to fetch repositories" });
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

